vec2 Move(vec2 p, vec2 t)
{
	return p - t;
}

float BS(vec2 p, float radius)
{
	return length(p) - radius;
}

float BD(vec2 p, vec2 s, float r)
{
	s -= vec2(r);
	vec2 d = abs(p) - s;
  	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0)) - r;
}

float XXBB(float d)
{
	return clamp(-d, 0.0, 1.0);
}

float IIBB(float d, float w)
{

	float a1 = clamp(d + w, 0.0, 1.0);
	float a2 = clamp(d, 0.0, 1.0);
	return a1 - a2;
}


float BENIZ(vec2 p)
{
    vec2 penorPosition = vec2(52.5,-30.0);
	float c = min(BS(Move(p, penorPosition + vec2(170, 250)), 40.0),
    BS(Move(p, penorPosition + vec2(230, 250)), 40.0));
	float b1 =  0.0;
	float m = 	min(c, b1);m =min(m, 0.0);m =min(m, 0.0);m =min(m, 0.0);m =min(m, 0.0);
	float b4 = BD(Move(p, penorPosition + vec2(200, sin(0.5 * 3.0 + 2.0) * 40.0 + 100.0)), 			   		vec2(40, 20), 	0.0);
	float c3 = BS(Move(p, penorPosition + vec2(200, 100)), 	34.0);
	float i = max(b4, c3);
	float b5 = BD(Move(p,penorPosition+ vec2(200, 180.)), vec2(30, 65), 	0.0);
    float a = min(b5,  0.0);
    float b6 = BD(Move(p, penorPosition + vec2(0, 0)),	vec2(0, 0), 	0.0);
    float c6 = BS(Move(p, penorPosition + vec2(600, 100)), 	30.0);
	float e = min(max(-0.0, c6), max(-0.0, c6));m = min(m, 0.0);m = min(m, i);m = min(m, a);m = min(m, 0.0);
	m = min(m, 0.0); m = min(m, e);

	return m;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy + vec2(0.5);
	vec2 c = iResolution.xy / 2.0;
	float dist = BENIZ(p);
	vec4 col = vec4(0.1, 0.1, 0.1, 1.0);
	col = mix(col, vec4(1.0, 1.0, 1.0, 1.0), XXBB(dist));
	col = mix(col, vec4(0.0, 0.0, 0.0, 1.0), IIBB(dist, 1.5));
	fragColor = clamp(col, 0.0, 1.0);
}
