//#define f(x) sin(x)
#define f(x) ( texture(iChannel0, (x)/64. ).rg * 2. - 1. )         // pattern source
#define C(x) ( 1. + atan(k*f(x))/atan(k) )                         // contrast func: linear to bi-lobe
#define H(i)  fract(sin( (i)* vec2(12.9898, 78.233)) * 43758.5453)
#define V(i)  ( H(i) + iTime*H(i+.5) )                             // random offset + velocity

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, 
         U = 20.*( 2.*u - R ) / R.y, C;
    float k = iMouse.z > 0. ? 16.*iMouse.y/R.y : 4.;               // contrast: const to bi-lobe 
    C = C(U+V(1.));                                                // base shape
    if (U.x>0.) C *= C(2.*U+V(2.)) * C(4.*U+V(3.))* C(8.*U+V(4.)); // cascade
    O = vec4( C.x*C.y );
    if (U.y<0.) O = exp(-O/4.);  // arbitrary tau                  // transparency
}
