#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy / 1. - .5;
    uv.x *= iResolution.x / iResolution.y;

    float f = 1. / length(uv);

    // add the angle
    f -= atan(uv.x, uv.y) / acos(.5);

    // let's roll
    f+= iTime*2.;

    // make it black and white
   	f = 1. - clamp(cos(f*PI) * dot(uv, uv) * iResolution.x / 1.+.5, .0, 1.);

    // add the darkness to the end of the tunnel
    //f *= sin(length(uv) + .5);

    fragColor = vec4(vec3(f), 1.0);
}
