renderpass.code:
#define S(a,s) smoothstep((1./iResolution.y)*2.*s,0.,a)
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define R(a,b) (b*rot(a))

float spheres(vec2 p,float rep) {
  p *= rep;
  p = fract(p)-.5;
  return 1.- S(length(p)-.02,10.);
}

float sdc(vec2 p, vec2 r) {
  vec2 q = abs(p)-r;
  return max(q.x,q.y);
}

float mp (vec3 p) {
  p.y+=sin(iTime);
  p.x+=cos(iTime*.2)*2.5;

  p.xy = R(iTime,p.xy);
  p.xz = R(iTime*.3,p.xz);
  p.yz = R(iTime*.2,p.yz);
  p = abs(p);
  p -=vec3(1.+sin(iTime)*.5);
  p.y+=sin(iTime)*.1;

  float d = length(p)-1.;
  d += sin(p.x*20.+iTime)*.01;
  d += cos(p.z*17.+iTime)*.03;
  d += cos(p.y*23.+.4+iTime)*.02;
  return d*.8;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 tv = (fragCoord/iResolution.y);
    vec3 blue = texture(iChannel0,uv+iTime).xyz;

    vec3 col = vec3(1.);
    col *= spheres(tv+floor(iTime)*.234,10.);

    float redbox = sdc(R(.3,uv)+vec2(.0,.6),vec2(1.1,.3));
    col = mix(col,vec3(1,0,.2), S(redbox,1.));

    float blackbox = sdc(R(-.3,uv)+vec2(.0,-.6),vec2(.1,.3));
    col = mix(col,vec3(0,0,0), .9*S(blackbox,3.));

    float stripes = sdc(fract(tv *15. + -tv.y + iTime)-.5,vec2(.3,.5));
    float sbound = sdc(uv+vec2(.65,.4),vec2(.2,.05));
    stripes = max(stripes, sbound);
    col = mix(col,1.-col, S(stripes,1.));
    col = mix(col, 1.-col, S(abs(sbound)-.002,1.));

    vec3 p,ro=vec3(-2,0,-10), rd=normalize(vec3(uv+blue.xy*.01,1));
    float i,d=0.,dd;
    for(i=0.;i<100.;i++){p=ro+rd*d;dd=mp(p);d+=dd;
    if (dd<0.001||d>100.)break;}

    float t = d/100.+i/100.;
    if (dd<0.001) col = (1.-col) + t;

    fragColor = vec4(col,1.0);
}
