void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0, uv) / float(iFrame + 1);
}

const float INF = 1e10;
const vec3 CAMERA_POS = vec3(0, 2, -6);
const vec3 LIGHT1_POS = vec3(-3, 0.85, 5);
const float LIGHT1_RADIUS = 0.5;
const vec3 LIGHT1_COLOR = vec3(1, 0, 1);

const vec3 LIGHT2_POS = vec3(3, 1, 2);
const float LIGHT2_RADIUS = 0.25;
const vec3 LIGHT2_COLOR = vec3(1, 0, 0);
vec3 randDir;


float mult = 1.0;

const int EMISSION = 0;
const int DIFFUSE = 1;
const int REFLECTION = 2;
const int REFRACTION = 3;

const float GLASS_N = 1.5;
const float AIR_N = 1.0;
float n1 = AIR_N;

//Реализация тумана внутри шара
float fogSdf(vec3 pos)
{
    return length(pos) - 0.75;
}

float BeerLambert(float absorptionCoefficient, float distanceTraveled)
{
    return exp(-absorptionCoefficient * distanceTraveled);
}

vec4 Fog(vec3 pos, vec3 dir)
{
    float t = fogSdf(pos);
    const float MARCH_SIZE = 0.01;
    float absorption = 10.0;
    float totalAbs = 0.0;
    float opacity = 1.0;
    for (int i = 0; i < 50; ++i) {
        vec3 curPos = pos + (float(i) * MARCH_SIZE + t) * dir;
        float sdf = fogSdf(curPos);
        if (sdf < 0.0) {
            float prevOpacity = opacity;
            opacity *= BeerLambert(absorption * (
            texture(iChannel3, curPos * 0.2).x
            + texture(iChannel3, curPos * 0.4).x
            + texture(iChannel3, curPos * 0.8).x
            + texture(iChannel3, curPos * 1.6).x
            ) * 0.25 * abs(sdf), MARCH_SIZE);
            float absorptionLastStep = prevOpacity - opacity;
            totalAbs += absorptionLastStep;
        }
    }
    return vec4(vec3(2, 2, 5) * totalAbs, 1.0 - opacity);
}



//Отрисовка стола
float tracePlane(vec3 pos, vec3 dir, out vec3 normal) {
    float t = (-1.2 - pos.y) / dir.y;
    if (t <= 0.0) {
        return INF;
    }

    vec3 worldPos = t * dir + pos;
    if (dot(worldPos.yz, worldPos.yz) >= 25.0) {
        return INF;
    }
    normal = vec3(0, 1, 0);
    return t;
}

//Отрисовка сферы(центрального шара)
float traceSphere(vec3 pos, vec3 dir, float r, out vec3 normal) {
    float a = dot(dir, dir);
    float b = dot(pos, dir);
    float c = dot(pos, pos) - r * r;
    float D = b * b - a * c;
    if (D < 0.0) {
        return INF;
    }
    float t = -b - sqrt(D);
    if (t > 0.0) {
        normal = normalize(pos + t * dir);
        return t;
    }
    t = -b + sqrt(D);
    if (t < 0.0) {
        return INF;
    }
    normal = normalize(pos + t * dir);
    return t;
}

//Отрисовка подставки
float traceCylinder(vec3 pos, vec3 dir, out vec3 normal){
    float t = (-1.0 - pos.y) / dir.y;
    if(t <= 0.0) {
        return INF;
    }
    vec3 worldPos = t * dir + pos;
    if(dot(worldPos.xz,worldPos.xz) < 1.0) {
        normal = vec3(0,1,0);
        return t;
    }

    float a = dot(dir.xz, dir.xz);
    float b = dot(pos.xz, dir.xz);
    float c = dot(pos.xz, pos.xz) - 1.0;
    float D = b*b - a*c;
    if (D < 0.0) {
        return INF;
    }
    t = (-b - sqrt(D)) / a;
    if (t > 0.0) {
        worldPos = t * dir + pos;
        if (worldPos.y <= -1.0) {
            normal = normalize(vec3(worldPos.x, 0, worldPos.z));
            return t;
        }
    }
    t = (-b + sqrt(D)) / a;
    if (t < 0.0) {
        return INF;
    }
    worldPos = t * dir + pos;
    if (worldPos.y <= -1.0) {
        normal = normalize(vec3(worldPos.x, 0, worldPos.z));
        return t;
    }
    return INF;

}

//Функция для создания теней
bool isOccluded(vec3 pos, vec3 target) {
    vec3 dir = target - pos;
    float dist = length(dir);
    dir /= dist;

    vec3 sphNorm;
    float sphT = traceSphere(pos, dir, 1.0, sphNorm);
    if (sphT < dist)
        return true;
    vec3 cylNorm;
    float cylT = traceCylinder(pos, dir, cylNorm);
    return (cylT < dist);
}

//Отрисовка света от источников
vec3 computeLight(vec3 pos, vec3 color, vec3 normal) {
    vec3 toLight1 = LIGHT1_POS - pos;
    float distSq1 = dot(toLight1, toLight1);
    float att1 = isOccluded(pos, LIGHT1_POS + randDir * LIGHT1_RADIUS) ? 0.0 : 20.0f / distSq1;
    vec3 toLight2 = LIGHT2_POS - pos;
    float distSq2 = dot(toLight2, toLight2);
    float att2 = isOccluded(pos, LIGHT2_POS + randDir * LIGHT2_RADIUS) ? 0.0 : 10.0f / distSq2;
    return color * (
        max(0.0, dot(normal, normalize(toLight1))) * att1 * LIGHT1_COLOR
        + max(0.0, dot(normal, normalize(toLight2))) * att2 * LIGHT2_COLOR
        + texture(iChannel1, normal).rgb * 0.1
    );
}

//Работа с рефракционным материалом
vec3 refraction(vec3 v, vec3 normal, float n1, float n2) {
    if (dot(v, normal) < 0.0) {
        normal = -normal;
    }
    float cosA = dot(v, normal);
    float sinA = sqrt(1.0 - cosA * cosA);
    vec3 tang = normalize(v - cosA * normal);
    float sinB = sinA / n2 * n1;
    float cosB = sqrt(1.0 - sinB * sinB);
    return sinB * tang + cosB * normal;
}

float pow2(float x) {
    return x * x;
}

float rand(float frame)
{
    return fract(sin( dot(vec3(frame), vec3(12.9898,78.233,45.5432) )) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 randVals = vec3(rand(float(iFrame)), rand(float(iFrame + 5)), rand(float(iFrame + 15)));
    randDir = normalize(randVals - 0.5);
    vec2 uv = (fragCoord - iResolution.xy * 0.5 + (randVals.xy - 0.5)) / iResolution.x;
    vec3 front = normalize(-CAMERA_POS);
    vec3 up = vec3(0, 1, 0);
    vec3 right = normalize(cross(front, up));
    up = normalize(cross(right, front));
    vec3 viewVec = normalize(front + right * uv.x + up * uv.y);

    float GLASS_R = pow2(AIR_N - GLASS_N) / pow2(AIR_N + GLASS_N);
    const float DIAMOND_N = 2.5;
    float DIAMOND_R = pow2(DIAMOND_N - GLASS_N) / pow2(DIAMOND_N + GLASS_N);

    vec3 curPos = CAMERA_POS;
    vec3 curDir = viewVec;
    vec3 colorMult = vec3(1, 1, 1);

    for (int i = 0; i < 10; ++i) {
        float t = INF;
        int materialType;
        vec3 color;
        vec3 normal;
        float nEnter = AIR_N;

        vec3 planeNorm;
        float planeT = tracePlane(curPos, curDir, planeNorm);
        if (planeT < t) {
            t = planeT;
            materialType = DIFFUSE;
            vec3 worldPos = t * curDir + curPos;
            color = texture(iChannel0, worldPos.xz * 0.1).rgb;
            normal = planeNorm;
        }
        vec3 l1Norm;
        float light1T = traceSphere(curPos - LIGHT1_POS, curDir, LIGHT1_RADIUS, l1Norm);
        if (light1T < t) {
            t = light1T;
            materialType = EMISSION;
            color = LIGHT1_COLOR;
            normal = l1Norm;
        }
        vec3 l2Norm;
        float light2T = traceSphere(curPos - LIGHT2_POS, curDir, LIGHT2_RADIUS, l2Norm);
        if (light2T < t) {
            t = light2T;
            materialType = EMISSION;
            color = LIGHT2_COLOR;
            normal = l2Norm;
        }
        vec3 sphNorm;
        float sphT = traceSphere(curPos, curDir, 1.0, sphNorm);
        if (sphT < t){
            t = sphT;
            normal = sphNorm;
           vec3 worldPos = t * curDir + curPos;
           if (randVals.x < GLASS_R ) {
                materialType = REFLECTION;
            } else if (randVals.x < GLASS_R * 18.0) {
                vec3 worldPos = t * curDir + curPos;
                materialType = EMISSION;
                vec4 fog = Fog(worldPos, viewVec);
                color = fog.rgb * fog.a * mult;
                mult *= 1.0 - fog.a;
            } else {
                materialType = REFRACTION;
                if (dot(curDir, normal) > 0.0) {
                    nEnter = AIR_N;
                } else {
                    nEnter = GLASS_N;
                }
            }
        }




        vec3 CylNorm;
        float CylT = traceCylinder(curPos, curDir, CylNorm);
        if (CylT < t) {
            t = CylT;
            materialType = DIFFUSE;
            vec3 worldPos = t * curDir + curPos;
            color = texture(iChannel2, worldPos.xz * worldPos.y).rgb;
            normal = CylNorm;
        }

        if (t != INF) {
            vec3 worldPos = t * curDir + curPos;
            fragColor = texture(iChannel0, worldPos.xz);
            if (materialType == EMISSION) {
                fragColor.rgb = color * colorMult;
                break;
            } else if (materialType == DIFFUSE) {
                fragColor.rgb = computeLight(worldPos, color, normal) * colorMult;
                break;
            } else if (materialType == REFLECTION) {
                curDir = reflect(curDir, normal);
                curPos = worldPos + curDir * 1e-5;
            } else if (materialType == REFRACTION) {
                curDir = refraction(curDir, normal, n1, nEnter);
                curPos = worldPos + curDir * 1e-5;
                n1 = nEnter;
            }
        } else {
            fragColor.rgb = texture(iChannel1, curDir).rgb * colorMult;
        }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv) + texture(iChannel1, uv);
}
