void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0, uv) / float(iFrame + 1);
}

const float INF = 1e10;
const vec3 CAMERA_POS = vec3(1.0, 1.2, -6);
const vec3 LIGHT1_POS = vec3(-2, 1, 3.5);
const float LIGHT1_RADIUS = 0.5;
const vec3 LIGHT1_COLOR = vec3(1, 1, 1);

const vec3 LIGHT2_POS = vec3(1.5, 0.25, -2);
const float LIGHT2_RADIUS = 0.2;
const vec3 LIGHT2_COLOR = vec3(0.5, 0.5, 1);
vec3 randDir;

const float CYLINDER_RADIUS = 1.0;

#define VERTEXCOUNT 12
#define TRIANGLECOUNT 20
#define PHI 1.618

vec3 vertices[VERTEXCOUNT] = vec3[VERTEXCOUNT] (
    vec3(-1,  PHI, 0) / 1.7,
    vec3( 1,  PHI, 0) / 1.7,
    vec3(-1, -PHI, 0) / 1.7,
    vec3( 1, -PHI, 0) / 1.7,

    vec3(0, -1,  PHI) / 1.7,
    vec3(0,  1,  PHI) / 1.7,
    vec3(0, -1, -PHI) / 1.7,
    vec3(0,  1, -PHI) / 1.7,

    vec3( PHI, 0, -1) / 1.7,
    vec3( PHI, 0,  1) / 1.7,
    vec3(-PHI, 0, -1) / 1.7,
    vec3(-PHI, 0,  1) / 1.7);

int triangles[TRIANGLECOUNT * 3] = int[TRIANGLECOUNT * 3] (
     5, 11,  0,
     1,  5,  0,
     7,  1,  0,
    10,  7,  0,
    11, 10,  0,

     9,  5,  1,
     4, 11,  5,
     2, 10, 11,
     6,  7, 10,
     8,  1,  7,

     4,  9,  3,
     2,  4,  3,
     6,  2,  3,
     8,  6,  3,
     9,  8,  3,

     5,  9,  4,
    11,  4,  2,
    10,  2,  6,
     7,  6,  8,
     1,  8,  9);

// (CAMERA_POS + t * viewVec).y == -1
// t = (-1 - CAMERA_POS.y) / viewVec.y
float tracePlane(vec3 pos, vec3 dir, out vec3 normal) {
    float t = (-1.2 - pos.y) / dir.y;
    if (t <= 0.0) {
        return INF;
    }
    vec3 worldPos = pos + t * dir;
    if (dot(worldPos.xz, worldPos.xz) >= 10.) {
        return INF;
    }
    normal = vec3(0, 1, 0);
    return t;
}

float traceSphere(vec3 pos, vec3 dir, float r, out vec3 normal) {
    // dot(pos + t * dir, pos + t * dir) == r * r;
    // dot(pos, pos) + 2 * t * dot(pos, dir) + t * t * dot(dir, dir) == r * r
    // t * t + 2.0 * t * dot(pos, dir) + dot(pos, pos) - r * r == 0
    float b = dot(pos, dir);
    float D = b * b - dot(pos, pos) + r * r;
    if (D < 0.0) {
        return INF;
    }
    float t = -b - sqrt(D);
    if (t > 0.0) {
        normal = normalize(pos + t * dir);
        return t;
    }
    t = -b + sqrt(D);
    if (t <= 0.0) {
        return INF;
    }
    normal = normalize(pos + t * dir);
    return t;
}

float traceTriangle(vec3 pos, vec3 dir, vec3 v0, vec3 v1, vec3 v2, out vec3 normal) {
    vec3 e1 = v1 - v0;
    vec3 e2 = v2 - v0;

    vec3 pvec = cross(dir, e2);
    float det = dot(e1, pvec);
    if (det < 1e-8 && det > -1e-8) {
        return INF;
    }

    float inv_det = 1.0 / det;
    vec3 tvec = pos - v0;
    float u = dot(tvec, pvec) * inv_det;
    if (u < 0.0 || u > 1.0) {
        return INF;
    }

    vec3 qvec = cross(tvec, e1);
    float v = dot(dir, qvec) * inv_det;
    if (v < 0.0 || u + v > 1.0) {
        return INF;
    }

    float t = dot(e2, qvec) * inv_det;
    normal = normalize((v0 + v1 + v2) / 3.0); 
    //normal = normalize(cross(e1, e2)); 
    if (t <= 0.0) {
        return INF;
    }
    return t;
}

float traceIcosahedron(vec3 pos, vec3 dir, out vec3 normal) {
    float t = INF;
    for (int i = 0; i < 20; ++i) {
        vec3 v0 = vertices[triangles[i * 3]];
        vec3 v1 = vertices[triangles[i * 3 + 1]];
        vec3 v2 = vertices[triangles[i * 3 + 2]];
        vec3 cur_norm;
        float cur_t = traceTriangle(pos, dir, v0, v1, v2, cur_norm);
        if (cur_t < t) {
            t = cur_t;
            normal = cur_norm;
        }
    }
    return t;
}

float traceCylinder(vec3 pos, vec3 dir, float r, out vec3 normal) {
    const float h = -0.9;
    float t = (h - pos.y) / dir.y;
    if (t <= 0.0) {
        return INF;
    }
    vec3 worldPos = pos + t * dir;
    if (dot(worldPos.xz, worldPos.xz) < r * r) {
        normal = vec3(0, 1, 0);
        return t;
    }

    float b = dot(pos.xz, dir.xz);
    float D = b * b - (dot(pos.xz, pos.xz) - r * r) * dot(dir.xz, dir.xz);
    if (D < 0.0) {
        return INF;
    }
    t = (-b - sqrt(D)) / dot(dir.xz, dir.xz);
    worldPos = pos + t * dir;
    if (worldPos.y < h && worldPos.y >= -1.2) {
        if (t >= 0.0) {
            normal = normalize(vec3(worldPos.x, 0, worldPos.z));
            return t;
        }
    }
    return INF;
}

bool isOccluded(vec3 pos, vec3 target) {
    vec3 dir = target - pos;
    float dist = length(dir);
    dir /= dist;

    /*vec3 sphNorm;
    float sphT = traceSphere(pos, dir, 1.0, sphNorm);
    if (sphT < dist) {
        return true;
    }
    */

    vec3 icoNorm;
    float icoT = traceIcosahedron(pos, dir, icoNorm);
    if (icoT < dist) {
        return true;
    }

    vec3 cylNorm;
    float cylT = traceCylinder(pos, dir, CYLINDER_RADIUS, cylNorm);
    return cylT  < dist;

}

vec3 computeLight(vec3 pos, vec3 color, vec3 normal) {
    vec3 toLight1 = LIGHT1_POS - pos;
    float distSq1 = dot(toLight1, toLight1);
    float att1 = isOccluded(pos, LIGHT1_POS + randDir * LIGHT1_RADIUS) ? 0.0 : 15.0 / distSq1;

    vec3 toLight2 = LIGHT2_POS - pos;
    float distSq2 = dot(toLight2, toLight2);
    float att2 = isOccluded(pos, LIGHT2_POS + randDir * LIGHT2_RADIUS) ? 0.0 : 20.0 / distSq2;
    return color * (max(0.0, dot(normal, normalize(toLight1))) * att1 * LIGHT1_COLOR +
        max(0.0, dot(normal, normalize(toLight2))) * att2 * LIGHT2_COLOR +
        texture(iChannel1, normal).rgb * 0.1);
}

vec3 refraction(vec3 v, vec3 normal, float n1, float n2) {
    if (dot(v, normal) < 0.0) {
        normal = -normal;
    }
    float cosA = dot(v, normal);
    float sinA = sqrt(1.0 - cosA * cosA);
    vec3 tang = normalize(v - cosA * normal);
    float sinB = sinA / n2 * n1;
    if (sinB > 1.0) {
        return reflect(v, normal);
    }
    float cosB = sqrt(1.0 - sinB * sinB);
    return sinB * tang + cosB * normal;
}

float pow2(float x) {
    return x * x;
}

float rand(float frame)
{
    return fract(sin( dot(vec3(frame), vec3(12.9898,78.233,45.5432) )) * 43758.5453);
}

float fogSdf(vec3 pos) {
    return length(pos) - 0.75;
}

float BeerLambert(float absorptionCoefficient, float distanceTraveled)
{
    return exp(-absorptionCoefficient * distanceTraveled);
}

float MARCH_SIZE = 0.01;
int MAX_STEPS = 50;
vec3 FOG_COLOR = vec3(0, 1, 1);
float ABSORPTION = 10.0;

vec4 sampleFog(vec3 pos, vec3 dir)
{
    float t = fogSdf(pos);
    const float MARCH_SIZE = 0.01;
    float opacity = 1.0;
    float totalAbs = 0.0;
    for (int i = 0; i < 50; ++i) {
        vec3 curPos = pos + (float(i) * MARCH_SIZE + t) * dir;
        float sdf = fogSdf(curPos);
        if (sdf < 0.0) {
            float prevOpacity = opacity;
            opacity *= BeerLambert(ABSORPTION * (
            texture(iChannel3, curPos * 0.2).r
            + texture(iChannel3, curPos * 0.4).r
            + texture(iChannel3, curPos * 0.8).r
            ) / 3.0 * abs(sdf), MARCH_SIZE);
            float absorptionLastStep = prevOpacity - opacity;
            totalAbs += absorptionLastStep;
        }
    }
    return vec4(FOG_COLOR * 4.0 * totalAbs, 1.0 - opacity);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 randVals =vec3(rand(float(iFrame)), rand(float(iFrame + 5)), rand(float(iFrame + 15)));
    randDir = normalize(randVals - 0.5);
    vec2 uv = (fragCoord - iResolution.xy * 0.5 + (randVals.xy - 0.5) * 2.0) / iResolution.x;
    vec3 front = normalize(-CAMERA_POS);
    vec3 up = vec3(0, 1, 0);
    vec3 right = normalize(cross(front, up));
    up = normalize(cross(right, front));
    vec3 viewVec = normalize(front + right * uv.x + up * uv.y);
    float mult = 1.0;

    const int EMISSION = 0;
    const int DIFFUSE = 1;
    const int REFLECTION = 2;
    const int REFRACTION = 3;

    vec3 curPos = CAMERA_POS;
    vec3 curDir = viewVec;
    const float DIAMOND_N = 2.42;
    const float AIR_N = 1.0;
    float n1 = AIR_N;
    float n2 = DIAMOND_N;
    float DIAMOND_R = pow2(AIR_N - DIAMOND_N) / pow2(AIR_N + DIAMOND_N);

    for (int i = 0; i < 5; ++i) {
        float t = INF;
        int materialType;
        vec3 color;
        vec3 normal;
        vec3 planeNorm;
        float planeT = tracePlane(curPos, curDir, planeNorm);
        if (planeT < t) {
            t = planeT;
            vec3 worldPos = curPos + t * curDir;
            color = texture(iChannel0, worldPos.xz * -0.25 ).rgb;
            normal = planeNorm;
            materialType = DIFFUSE;
        }
        vec3 l1Norm;
        float light1T = traceSphere(curPos - LIGHT1_POS, curDir, LIGHT1_RADIUS, l1Norm);
        if (light1T < t) {
            t = light1T;
            color = LIGHT1_COLOR;
            normal = l1Norm;
            materialType = EMISSION;
        }
        vec3 l2Norm;
        float light2T = traceSphere(curPos - LIGHT2_POS, curDir, LIGHT2_RADIUS, l2Norm);
        if (light2T < t) {
            t = light2T;
            color = LIGHT2_COLOR;
            normal = l2Norm;
            materialType = EMISSION;
        }
        /*vec3 sphNorm;
        float sphT = traceSphere(curPos, curDir, 1.0, sphNorm);
        if (sphT < t) {
            t = sphT;
            normal = sphNorm;
            if (randVals.x < DIAMOND_R) {
                materialType = REFLECTION;
            } else {
                materialType = REFRACTION;
            }
        }*/
        vec3 icoNorm;
        float icoT = traceIcosahedron(curPos, curDir, icoNorm);
        if (icoT < t) {
            t = icoT;
            normal = icoNorm;
            if (randVals.x < DIAMOND_R) {
                materialType = REFLECTION;
            } else {
                materialType = REFRACTION;
            }
            //color = vec3(1, 1, 0);
            //materialType = DIFFUSE;
        }
        vec3 cylNorm;
        float cylT = traceCylinder(curPos, curDir, CYLINDER_RADIUS, cylNorm);
        if (cylT < t) {
            t = cylT;
            vec3 worldPos = curPos + t * curDir;
            color = texture(iChannel2, worldPos.xz * worldPos.y).rgb * vec3(1, 0.75, 0.5);
            normal = cylNorm;
            materialType = REFLECTION;
        }
        if (t != INF) {
            vec3 worldPos = curPos + t * curDir;
            //fragColor.rgb = curDir;
            if (materialType == EMISSION) {
                fragColor.rgb = color;
                break;
            } else if (materialType == DIFFUSE) {
                fragColor.rgb += mult * computeLight(worldPos, color, normal);
                break;
            } else if (materialType == REFLECTION) {
                fragColor.rgb += mult * computeLight(worldPos + t * curDir, color, normal);
                curDir = reflect(curDir, normal);
                curPos = worldPos + curDir * 1e-5;
            } else if (materialType == REFRACTION) {
                curDir = refraction(curDir, normal, n1, n2);
                curPos = worldPos + curDir * 1e-5;

                 vec4 fog = sampleFog(curPos, curDir);
                fragColor.rgb += fog.rgb * fog.a * mult;
                mult *= 1.0 - fog.a;

                float tmp = n1;
                n1 = n2;
                n2 = tmp;
            }
        } else {
            fragColor += mult * texture(iChannel1, curDir);
            break;
        }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = texture(iChannel0, uv) + texture(iChannel1, uv);
}
