#define ITERS 9 //normal world map
//#define ITERS 6 //swamp world


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Scale and move to make things a litle more interesting t look at.
    float scale = 2000.0;
    float trans = iTime * 200.0;
    vec2 coord = (scale * fragCoord/iResolution.xy) + vec2(trans+30000.0,0.0);

    int val = 0;
    float result = 0.0;
    vec3 col = vec3(0.0);
    vec3 col_prev = vec3(0.0);
    for(int i = 0; i < ITERS; i++){
        col_prev = col;
        coord.y -= (4.0-result);
        coord += coord.yy/8.0;
        coord = coord.yx/(4.0);
        result = ((result + float(val = ((int(coord.x-coord.y/2.0) & int(coord.y+coord.x/2.0)) % 3)))/(2.0));
        col.x = result;
        col = ((col.yzx)*2.0+col_prev)/3.;
    }
    // Output.
    fragColor = vec4((col),0.0);
}
