//#define ITERS 9 //normal world map
#define ITERS 12 //swamp world


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Scale and move to make things a litle more interesting t look at.
    float scale = 2000.0;
    float trans = iTime * 500.0;
    vec2 coord = (scale * fragCoord/iResolution.xy) + vec2(trans+30000.0,0.0);

    // Heart of color selection.
    int val = ((int(coord.x) & int(coord.y)) % 3);
    float result = 0.0;
    vec3 col = vec3(0.0);
    vec3 col_prev = vec3(0.0);
    for(int i = 0; i < ITERS; i++){
        col_prev = col;
        coord.y -= (4.0-result);
        coord += coord.yy/8.0;
        coord = coord.yx/(3.0)+col.xy;
        //coord.y *= -1.0;
        result = ((result + float(val = ((int(coord.x/1.5-coord.y/2.0) & int(coord.y/1.5+coord.x/2.0)) % 3)))/(2.0));
        col.x = result;
        col = ((col.yzx)*2.0+col_prev)/3.0;
    }
    // Output.
    fragColor = vec4((col),0.0);
}
