float C( vec2 uv, vec2 pos, float rad, float blur ) {
    return smoothstep(rad, rad-blur, length(uv-pos));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy/iResolution.xy;

    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;


    vec2 mouse = iMouse.xy/iResolution.xy;
    mouse -= .5;

    float col = C((uv-mouse*0.02), vec2(0), .3, .01);

    col -= C((uv-mouse*0.035), vec2(-.1, .15), .07, .01);
    col -= C((uv-mouse*0.035), vec2(.1, .15), .07, .01);

    col += C((uv-mouse*0.065), vec2(-.1, .15), .03, .01);
    col += C((uv-mouse*0.065), vec2(.1, .15), .03, .01);


    col -= C((uv+mouse*0.035), vec2(0, -.1), .1, .01);

    fragColor = vec4(vec3(col), 1.);
}
