void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float gridSize = 150.0 + sin(iTime) * 100.0;
    float radius = gridSize * (1.0 - (sin(iTime) + 1.0) / 2.0);

    vec2 coord = fragCoord - iResolution.xy * 0.5;
    vec2 cell = floor(coord / gridSize);
    vec2 center = cell * gridSize + vec2(gridSize * 0.5);
    if (length(coord - center) < radius) {
        fragColor = vec4(
            (sin(cell.x + iTime) + 1.0) / 2.0,
            (cos(cell.y + iTime) + 1.0) / 2.0,
            1.0,
            1.0);
    } else {
        fragColor = vec4(0.0);
    }
}
