vec2 multiply_complex_numbers(vec2 a, vec2 b){
    return vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.x*b.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((fragCoord.xy)-.5*iResolution.xy)/iResolution.y*4.;
    vec3 col = vec3(0);
    vec2 z = uv;
    vec2 c = vec2(sin(iTime/3.)/2., cos(iTime/3.)/2.);
    for(float i = 0.; i < 100.; i++){
        z = multiply_complex_numbers(z, z) + c;
        if(length(z) > 2.){
            col = vec3(0);
            if(i > 5.)col = vec3(i/100., 1.-i/100.,0.);
            break;
        }
    }
    fragColor = vec4(col,1.0);
}
