float seed;
float rand()
{
    seed++;
    return hash11(seed);
}


mat2 r2d(float a) { float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }
float _cube(vec3 p, vec3 s)
{
    vec3 l = abs(p)-s;
    return max(l.x, max(l.y, l.z));
}
float _sqr(vec2 p, vec2 s)
{
    vec2 l = abs(p)-s;
    return max(l.x, l.y);
}

float getLetter(vec2 p, vec2 pos)
{
    vec2 off = (vec2(rand(), rand())-.5)*3.*vec2(1., -1.)/(iResolution.x*1.75);

    vec2 stp = vec2(1./16.);
    vec2 coords = clamp((p.xy*vec2(-1.))*.02, vec2(0.), stp)+stp*pos+off;
    float sdf = texture(iChannel0, coords).x-.1;
    return -sdf;
}

vec2 map(vec3 p)
{
    p.xy -= vec2(6.2,1.5);
    vec2 acc = vec2(10000.,-1.);

    vec2 off = vec2(1.5,0.);
    acc = _min(acc, vec2(getLetter(p.xy+off*0., vec2(7.,11.)), 0.));
    acc = _min(acc, vec2(getLetter(p.xy+off*1., vec2(5.,11.)), 1.));
    acc = _min(acc, vec2(getLetter(p.xy+off*2., vec2(14.,11.)), 2.));
    acc = _min(acc, vec2(getLetter(p.xy+off*3., vec2(5.,10.)), 3.));
    acc = _min(acc, vec2(getLetter(p.xy+off*4., vec2(1.,11.)), 4.));
    acc = _min(acc, vec2(getLetter(p.xy+off*5., vec2(2.,10.)), 5.));
    acc = _min(acc, vec2(getLetter(p.xy+off*6., vec2(9.,10.)), 6.));
    acc.x = max(acc.x, abs(p.z)-.5);

    acc = _min(acc, vec2(-p.y-.675, 7.));
    return acc;
}

vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.01, 0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}

vec3 trace(vec3 ro, vec3 rd, int steps)
{
    vec3 p = ro;
    for (int i = 0; i < steps && distance(p, ro) < 15.; ++i)
    {
        vec2 res = map(p);
        if (res.x < 0.01)
            return vec3(res.x, distance(p, ro), res.y);
        p+=rd*res.x;
    }
    return vec3(-1.);
}

vec3 getMat(vec3 res, vec3 rd, vec3 p, vec3 n)
{
    vec3 lpos = vec3(-10.,-10.,10.);
    vec3 ldir = lpos-p;
    float ndotl = dot(normalize(ldir), n);
    n.xy *= r2d(iTime+p.x);
    vec3 col = n*.5+.5;
    if (abs(dot(n, vec3(0.,0.,1.))) > 0.75)
        col = vec3(0.);

    if (res.z == 7.)
        col = vec3(.01)+vec3(0.286,0.286,0.831)*sat(ndotl)*.25;
    return col;
}

vec3 rdr(vec2 uv)
{
    vec3 col = vec3(1.);
    float zoom = 12.;
    vec3 ro = vec3(sin(iTime),-4.,-5.);
    vec3 ta = vec3(0.,0.,0.);
    vec3 rd = normalize(ta-ro);
    ro -= zoom*vec3(uv.xy, 0.);
    //rd = getCam(rd, uv);
    vec3 res = trace(ro, rd, 128);
    if (res.y > 0.)
    {
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);
        col = getMat(res, rd, p, n);
            for (int i = 0; i < 1; ++i)
            {
                vec3 refl = normalize(reflect(rd, n));
                float spec = 1.;

                refl = mix(refl, normalize(vec3(rand(), rand(), rand())-.5), spec);

                if (dot(refl, n) < 0.01)
                    refl = -refl;
                vec3 resrefl = trace(p+n*0.01, refl, 64);
                if (resrefl.y > 0.)
                {
                    vec3 prefl = p+refl*resrefl.y;
                    vec3 nrefl = getNorm(prefl, resrefl.x);
                    col += getMat(resrefl, refl, prefl, nrefl);
                    n = nrefl;
                    p = prefl;
                }
                else
                {
                    ;//col += getEnv(refl);
                    break;
                }
                rd = refl;
            }
    }
    col *= 2.5/(col+1.);
    col = pow(col, vec3(1.7));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;
    seed=texture(iChannel1,uv).x;
    seed+=fract(iTime);
    vec2 off = (vec2(rand(), rand())-.5)*3.*vec2(1., -1.)/(iResolution.x*1.75);
    vec3 col = rdr(uv+off);
    col = sat(col);
    //col = texture(iChannel0, fragCoord/iResolution.xy).xxx;
//    col = mix(col, vec3(1., 0., 0.), 1.-sat((abs(length(uv)-.25)-0.001)*400.));
    col = mix(col, texture(iChannel2, fragCoord/iResolution.xy).xyz, .9);

    fragColor = vec4(col,1.0);
}

