#define R(p,a,t) mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a)
#define H(h) (cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    vec3 p,r=iResolution,c=vec3(0),
    d=normalize(vec3((C-.5*r.xy)/r.y,1));
    float i=0.,s,e,g=0.,t=iTime;
	for(;i++<99.;){
        p=g*d;
        p.z-=.3;
        p=R(p,cos(t+vec3(1,8,3)),clamp(sin(t*.5)*3.,-5.,.5));
        s=3.;
        for(int j=0;j++<7;)
            p=abs(p.zxy),
            s*=e=2./min(20.,dot(p,p)),
            p=p*e-vec3(.1,.2,.8);
        g+=e=min(length(p.z)+.3,length(p.yz)-.1)/s;
        c+=mix(vec3(1),H(log(s)*.2+t*.2),H(log(s)*.5))*.01/exp(i*i*e);  
    }
    O=vec4(c,1);
}
