float N11(float p){
 	float x=fract(sin(p*100.+iTime)*2368.); 
    return x;
}
float N21(vec2 p){
 	float x=fract(sin(p.x*100.+p.y*291.)*2368.); 
    return x;
}
float disY(vec2 p,vec2 q){
    return abs(p.y-q.y);
}
float disX(vec2 p,vec2 q){
    return abs(p.x-q.x);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 scale = 10.*vec2(iResolution.x/iResolution.y,1.0);
    vec2 newUV=uv*scale;
    vec2 id=floor(newUV);
    vec2 offset=fract(newUV);
    vec2 maskUV=id/scale;
    // Time varying pixel color
    vec3 col=vec3(0.0);
    vec2 lastUV = maskUV;
    vec2 tempScale=vec2(1);
        for(int j=-6;j<=6;j++){

            vec2 idOffset = vec2(j,0);
            vec2 newid= id+idOffset;
            vec2 newid_rand = vec2(N21(newid),N11(newid.y));
            vec2 maskScale=newid_rand*vec2(6.5,0.5)+vec2(0.0,1.0);
            //float newid_rand = N21(newid);
            //float maskScale=newid_rand*0.5+1.0;
            if(disX(offset,idOffset+0.5)*2.<maskScale.x&&tempScale.x<maskScale.x)
            {
                tempScale.x=maskScale.x;
                lastUV=newid/scale;

            }
        }
    // Output to screen
    col = texture(iChannel0,lastUV).rgb;
    //col = vec3(tempScale,0.0);
    fragColor = vec4(N21(lastUV));
}
