void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // GAUSSIAN BLUR SETTINGS {{{ (stolen from https://www.shadertoy.com/view/Xltfzj)
    float Directions = 16.0; // BLUR DIRECTIONS (Default 16.0 - More is better but slower)
    float Quality = 4.0; // BLUR QUALITY (Default 4.0 - More is better but slower)
    float Size = 20.0; // BLUR SIZE (Radius)
    // GAUSSIAN BLUR SETTINGS }}}

    vec2 rad = Size/iResolution.xy;
    float PI2 = 6.283185;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;

    // Blur calculations
    for( float d = 0.0; d < PI2; d += PI2 / Directions)
    {
		for(float i = 1.0 / Quality; i <= 1.0; i += 1.0 / Quality)
        {
			col += texture( iChannel0, uv + vec2(cos(d), sin(d)) * rad * i).rgb;		
        }
    }

    // Output to screen
    col /= Quality * Directions;
    fragColor =  vec4(col, 1.0);
}
