void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord - iResolution.xy)/iResolution.x;

    float d = length(uv);
    float c = smoothstep(0.3, 0.32, d);

    if (uv.y <= 0.0) {
        float a =1.0 - (smoothstep(0.3, 0.31, d) - smoothstep(0.31, 0.32, d));
        c += a;
    }

    // left white circle
    float d1 = length(uv - vec2(-0.15, 0.0));
    c += 1.0 - smoothstep(0.153, 0.16, d1);

    // right black circle
    float d2 = length(uv - vec2(0.15, 0.0));
    c *= smoothstep(0.15, 0.16, d2);

    // left black dot
    float d3 = length(uv - vec2(-0.15, 0.0));
    c *= smoothstep(0.04, 0.05, d3);

    // right white dot
    float d4 = length(uv - vec2(0.15, 0.0));
    c += 1.0 - smoothstep(0.04, 0.05, d4);

    vec3 col = vec3(c);

    // Output to screen
    fragColor = vec4(col,1.0);
}
