    float Circle(vec2 uv, vec2 p, float r, float blur)
{
                float   d = length(uv-p);
                 float   c = smoothstep(r, r-blur, d);
                   return c;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
                  vec2 uv  = 1.0-2.0*fragCoord/iResolution.y;

                  vec3 col = vec3(0.0);



                   float orange = Circle(uv, vec2(0.2, -0.5), 0.3, 0.01);
                    orange -= Circle(uv, vec2(0.081, -0.54), 0.07, 0.01);
                     col = vec3(1,0.6,0)*orange;

                   float blue = Circle(uv, vec2(-0.1, -0.1), 0.15, 0.01);
                    blue += Circle(uv, vec2(-0.2, 0.5), 0.15, 0.01);
                    blue += Circle(uv, vec2(-1.0, 0.5), 0.15, 0.01);
                     col += vec3(0,0,1)*blue;

                   float green = Circle(uv, vec2(-0.2, 0.2), 0.15, 0.01);
                    green += Circle(uv, vec2(-1.0, 0.2), 0.15, 0.01);
                     col += vec3(0,1,0)*green;

                   float red = Circle(uv, vec2(-0.6, 0.2), 0.25, 0.01);
                    red += Circle(uv, vec2(0.5, -0.4), 0.05, 0.01);
                     col += vec3(1,0,0)*red;

                   float white = Circle(uv, vec2(0.1, -0.55), 0.1, 0.01);
                    white -= Circle(uv, vec2(0.081, -0.54), 0.07, .01);
                     col += vec3(1,1,1)*white;

                   float goluboy = Circle(uv, vec2(-1.2, -0.05), 0.18, 0.01);
                     col += vec3(0,1,1)*goluboy;



                      fragColor = vec4(col,1.0);     
}
