vec4 col1 = vec4(1.0,0.0,0.0,0.0);
vec4 col2 = vec4(1.0,4.0,0.0,0.0);
vec4 col3 = vec4(1.0,0.0,8.0,0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 xy = fragCoord.xy / iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;

    float h = 0.5; // adjust position of middleColor
    vec4 col5 = mix(mix(col1, col2, xy.x/h), 
    mix(col2, col3, (xy.x - h)/(1.0 - h)), step(h, xy.x));
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx);
    // vec3 col = questionTwo(fragCoord);
    // vec3 col = questionThree(fragCoord);
    fragColor = vec4(col5), vec4(col, 1.0);
    //fragColor = vec4(col,1.0);
}
