float circle(vec2 pos, float rad, float smoothing) {
	return smoothstep(rad, rad + smoothing, length(pos));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Centering
    vec2 position = vec2(uv-0.5);

    //Resolution fix
    position.x = position.x * (iResolution.x / iResolution.y); 

    float radius = 0.5;
    float smoothing = 0.004;

    float circ = circle(position, radius, smoothing);
    vec3 col = vec3(circ);

    // Output to screen
    fragColor = vec4(col,1.0);
}
