float getSoftNoise(float x) {
    float wave = sin(x / 32.)
            + sin(x)
            + cos(2. * x) / 2.
            + sin(4. * x) / 4.
            + sin(8. * x) / 8.
            + sin(16. * x) / 16.
            + sin(32. * x) / 32.;
    return wave / 1000.;
}

float getStarSwitch(float x) {
    float zoom = 1000000.;
    float softNoise = getSoftNoise(x * zoom);
    float density = .000001;

    if (softNoise < 0. && softNoise > -density) {
        return -softNoise * (1. / density) * sin(
            iTime + sin(x * 50.) * 5.
        );
    }

    return 0.;
}

float getStarPattern(vec2 uv) {
    float voidSwitch = (
        getStarSwitch(uv.x + cos(uv.x) * sin(uv.y)) +
        getStarSwitch(uv.y + sin(uv.x) * cos(uv.y) / 20.)
    );

    return voidSwitch;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy - .5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = getStarPattern(uv) * vec3(1, 1, 2);

    fragColor = vec4(col, 1);
}
