void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    vec2 ab = vec2(0);
    ab.x = atan(uv.x, uv.y)/6.28*4.0;
    ab.y = length(uv);

    ab.y = 1.0/ab.y;
    ab.y += iTime;
    ab.x -= iTime*0.5;
    ab.x += ab.y*0.3;

    vec4 col = texture(iChannel1, fract(ab));

    // Output to screen
    fragColor = vec4(col);
}
