vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

vec2 triangle_wave(vec2 a){
    //a = rotate(a,num*radians(180.));
    vec2 to_return = abs(fract((a+vec2(1.,0.5))*1.5)-.5);
    //to_return /= 1.5; //makes another interesting pattern
    return to_return;
    //return abs(fract((a+vec2(1.,1.5)+num)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 8.*1.5;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
        float time1 = iTime/64.;

    uv += vec2(time1/2.0,time1/3.0)/t1/4.0+(vec2(cos(time1),sin(time1)))*8./t1;
    float scale = 1.5;
    float offset = 0.;
    float offset1 = iTime/1000.;
        vec2 t2 = vec2(0.);
        vec2 t3 = vec2(0.);
        for(int k = 0; k < 9; k++){

            uv += t2;
            uv /= scale;
            t2 = triangle_wave(uv);
            t3 = -triangle_wave(uv.yx-.5);
            uv = t2-t3;

            col.x = (col.x+max(uv.y-uv.x,col.x))/(2.);
            col.x = abs(col.x-.5);
            col = abs(col.yzx-vec3(col.x/sqrt(2.)))*2.;
        }

    fragColor = vec4(col*2.,1.0);
}
