    bool circle(vec2 p, vec2 c, float r)
    {
           float scaleY = iResolution.y / iResolution.x;
             return (p.x - c.x) * (p.x - c.x) + (p.y   - c.y) * (p.y - c.y) * (scaleY * scaleY) < r*r; 
    }
    bool ellips(vec2 p, vec2 c, float r, float a, float b)
    {
         float t = iResolution.y / iResolution.x;
           return (p.x-c.x)*(p.x-c.x)*0.5/(a*a) + (p.y-c.y)*(p.y-c.y)*0.5*(t*t)/(b*b) < r*r; 
    }

    float calculate(vec2 a, vec2 b, vec2 p)
    {
             return (p.x-a.x)*(b.y-a.y) - (p.y-a.y)*(b.x-a.x);
    }

        bool isOnOneSide(vec2 a, vec2 b, vec2 c, vec2 p) 
        {
                return calculate(a, b, c)*calculate(a, b, p) >= 0.0;
        }

            bool triangle(vec2 a, vec2 b, vec2 c, vec2 p)
            {
                     return  isOnOneSide(a, b, c, p) && isOnOneSide(b, c, a, p) && isOnOneSide(c, a, b, p);
            }

