    bool rectangle(vec2 a, vec2 b, vec2 point){
     return((point.x > a.x && point.x < b.x) && (point.y < a.y && point.y > b.y));//a – верхний правый угол, b – нижний левый угол 
    }

    bool triangle (vec2 a, vec2 b, vec2 c, vec2 point){
      //данное условие определяет принадлежит ли точка треугольнику
      float z1 = (a.x - point.x) * (b.y - a.y) - (b.x - a.x) * (a.y - point.y);
      float z2 = (b.x - point.x) * (c.y - b.y) - (c.x - b.x) * (b.y - point.y);
      float z3 = (c.x - point.x) * (a.y - c.y) - (a.x - c.x) * (c.y - point.y); 
       return((z1 >= 0. && z2 >=0. && z3 >= 0.) || (z1 <= 0. && z2 <= 0. && z3 <= 0.));  
    } 

    bool ellipse (vec2 center, vec2 point, vec2 coefficient, float r){
      float scaleY = iResolution.y / iResolution.x;
       return ((coefficient.x * (point.x - center.x) * (point.x - center.x) + coefficient.y * (point.y - center.y) * (point.y - center.y) * (scaleY * scaleY)) <= r);
    }

    bool circle(vec2 p, vec2 c, float r){
    // нормализируем экран для того, чтоб круг был кругом, а не элипсом 
    float scaleY = iResolution.y / iResolution.x;
     return (p.x - c.x) * (p.x - c.x) + (p.y   - c.y) * (p.y - c.y) * (scaleY * scaleY) < r*r; 
    }

