vec3 drawTriangle( vec3 P , vec3 A, vec3 B, vec3 C ){
    //       A
    //     |    \
    //    |      \
    //   |   . p  \
    //  |          \
    // B ----------  C 

    vec3 AB = A - B;
    vec3 BC = B - C;
    vec3 CA = C - A;

    vec3 AP = A - P;
    vec3 BP = B - P;
    vec3 CP = C - P;

    bool b1 = cross(AB, AP).z   >= 0.0;
    bool b2 = cross(BC, BP).z   >= 0.0;
    bool b3 = cross(CA, CP).z   >= 0.0;

    if(b1 && b2 && b3  ){
      return vec3(1.0);
    }else{
      return vec3(0.0);
    }
}//code stolen from  yyf1994 (draw_triangle)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;  //uv Range<0,1>

  //uv point position
  uv -= vec2(.2,.5);
  uv.x *= iResolution.x/iResolution.y;

   //☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯☯
   float circle = length(uv) + sin(iTime * 5.)*.01;  //circle
   if (circle >.15)circle = 0.;else circle = 1.;     //circle length

   //■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■ ■
   float hhhhhh;
   float vvvvvv;
   if (uv.x >  .40 + sin(iTime * 5.)*.01 && uv.x < .65 - sin(iTime * 5.)*.01 )hhhhhh=1.;else hhhhhh = 0.;  //spuare horizontal & vertical
   if (uv.y > -.12 + sin(iTime * 5.)*.01 && uv.y < .13 - sin(iTime * 5.)*.01 )vvvvvv=1.;else vvvvvv = 0.;
   float square = hhhhhh * vvvvvv;

  //▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲▲
  //code stolen from  yyf1994 (draw_triangle)
    vec3 v0 = vec3( 1.030000000000000000000000,   0.15 + sin(iTime * 5.)*.01 ,  0.0);
    vec3 v1 = vec3( 0.88 + sin(iTime * 5.)*.01,  -0.100000000000000000000000 ,  0.0);
    vec3 v2 = vec3( 1.18 - sin(iTime * 5.)*.01,  -0.100000000000000000000000 ,  0.0);

    vec3 triangle = drawTriangle(vec3(uv,0.0),v0,v1,v2);

   vec3 col = vec3(1,0,1);  //purple
   fragColor = vec4((square + circle + triangle) * col,1.0);
}
