float shift;

float edgeGlow(vec2 _uv, float shiftX, float shiftY){

    _uv += vec2(shiftX*shift, shiftY*shift)/iResolution.xy;// Anton, don't forget to replace iResolution here
    vec4 pict = texture(iChannel0, _uv);// Anton, don't forget to replace iChannel0 here
    return 0.2126*pict.r + 0.7152*pict.g + 0.0722*pict.b;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 vTextureCoord = fragCoord/iResolution.xy;;
    vec2 resolution = iResolution.xy;
    float time = iTime;

    vec2 uv_stable = vTextureCoord;

    shift = 4.0; // Anton, this is the WIDTH of edge glowing

    float speed = 0.5;//Anton, this defines duration of each type of effect (blinking, shift to right, shift to left). Blinking has twice the duration of the other effects.
    float halfspeed = speed*0.5;

    vTextureCoord.x -= floor(mod(time*halfspeed, 2.))*(0.125-(floor(mod(time*speed, 2.))*0.185));
    float blink = clamp(floor(mod(time*15., 2.))+floor(mod(time*halfspeed, 2.)), 0.,1.);


    float Gx = 0.;
    Gx += -edgeGlow(vTextureCoord, -1., -1.);
    Gx += -2.*edgeGlow(vTextureCoord, -1., 0.);
    Gx += -edgeGlow(vTextureCoord, -1., 1.);
    Gx += edgeGlow(vTextureCoord, 1., -1.);
    Gx += 2.*edgeGlow(vTextureCoord, 1., 0.);
    Gx += edgeGlow(vTextureCoord, 1., 1.);

    float Gy = 0.;
    Gy += -edgeGlow(vTextureCoord, -1., -1.);
    Gy += -2.*edgeGlow(vTextureCoord, 0., -1.);
    Gy += -edgeGlow(vTextureCoord, 1., -1.);
    Gy += edgeGlow(vTextureCoord, -1., 1.);
    Gy += 2.*edgeGlow(vTextureCoord, 0., 1.);
    Gy += edgeGlow(vTextureCoord, 1., 1.);

    float effect = (Gx*Gx + Gy*Gy);
    vec3 mask = texture(iChannel1, vTextureCoord).rgb;
    vec3 effectcolor = vec3(1.)-vec3(1., 0.5, 0.0)*(floor(mod(time*halfspeed, 2.))*((floor(mod(time*speed, 2.)))));

    fragColor = vec4(texture(iChannel0, uv_stable).rgb+vec3(effect)*effectcolor*mask.r*blink,1.0);

}
