void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;


   float pi = 6.28318530718;
    float rad = atan(uv.y,uv.x);
    float deg = rad > 0. ? rad*360./pi : (pi + rad) * 360. / pi;

    float c1 = abs((1./360.)*deg-0.5)*6.-1.;
    float c2 = -abs((1./360.)*deg-2./6.)*6.+2.;
    float c3 = -abs((1./360.)*deg-4./6.)*6.+2.;
    vec3 c4 = clamp(vec3(c1,c2,c3),.0,1.);

    c4 = c4 * smoothstep(1.,.99,length(uv.xy))-smoothstep(.81,.8,length(uv.xy));

    // Output to screen
    fragColor = vec4(c4,1.0);
}
