float Circle(vec2 uv, vec2 p, float r, float blur)
{
    float d = length(uv - p); // Distance from origin
    float c = smoothstep(r, r - blur, d);

    return max(c, 0.0);
}


vec3 GetWave(vec2 uv, float amp, float angle, float brightness)
{
    uv.x *= iResolution.x / iResolution.y;
    // Time varying pixel color
    float c = 0.0f;
    uv.x *= amp;
    uv.y *= amp;
    uv.x += sin(uv.y * 2.8);
    float cd = cos(radians(angle)) * uv.x + sin(radians(angle)) * uv.y - iTime;
    cd = fract(cd);
    c = smoothstep(1.0, 0.0, cd) * float(cd > 0.5) + brightness;
    c += smoothstep(0.0, 1.0, cd) * float(cd <= 0.5) + brightness;

    vec3 col = vec3(c);

    return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;



    uv -= 0.5;
    float ratio = iResolution.x / iResolution.y;
    uv.x *= ratio;
    // Time varying pixel color
    vec3 bg = vec3(0.0, 0.415, 0.305);
    vec3 red = vec3(0.95, 0.165, 0.254);
    float mask = Circle(uv, vec2(-0.25, 0.0), 0.2 * ratio, 0.01);

    vec4 flagColor = vec4(vec3(mask) * red, 1.0) - vec4(vec3(mask) * bg, 1.0) + vec4(bg, 1.0);
    vec3 wave = GetWave(uv, 0.9, 60.0, 0.2);

    fragColor = flagColor * vec4(wave, 1.0);
}
