mat2 Rot2(float a ) 
{
    float c = cos( a );
    float s = sin( a );
    return mat2( c, -s, s, c );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.);
    float angle = (0.3333)*3.1415*0.5;
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    uv*=1.5;
    uv *= Rot2(iMouse.x*0.002);

    uv.x = abs(uv.x);
    uv.x -= .075;

    uv.y = abs(uv.y);
    uv.x -= .5;
    uv *= Rot2(0.16667*3.1415);
    uv.x += .5;


    uv *= 3.;
    uv.x = abs(uv.x); 
    uv.x -= .5;

    uv *= -Rot2(angle);
    uv.x = abs(uv.x);
    uv *= Rot2(angle); 



    float b = 1.;
    for(float i = 0.; i < floor(iMouse.y*0.01); i++)
    {
        uv *= 3.;
        uv.x -= 1.5;

        uv.x = abs(uv.x); 
        uv.x -= .5;

        b *= -1.;

        uv *= -Rot2(b*angle);
        uv.x = abs(uv.x);
        uv *= Rot2(b*angle); 
    }


    float d = length(uv - vec2(clamp(uv.x, -1., 1.), 0.));
    col += smoothstep(pow(floor(iMouse.y*0.01),3.)*0.01+0.01, 0., d);

    fragColor = vec4(col,1.0);
}
