// analysing filtering of "The Typist" by Xor. https://shadertoy.com/view/sd3czM

#define T(U)         vec4( texture(iChannel0, (U)/R).rgb, 1)
#define rot(a)       mat2( cos(a), sin(a), -sin(a), cos(a) )
#define GOLDEN_ANGLE 2.3999632


//#define sampling  16.
//#define sampling  max(2., 69.*abs(p.x))
  #define sampling ( U.x < R.x/2. ? 16. :  max(2., 69.*abs(p.x)) )

void mainImage(out vec4 O, vec2 U)
{
    vec2  R = iResolution.xy,
          p = vec2( dot( U+U-R , vec2(1,-5)/5e3 ), 0); // projection of direction along view axis 
    float r = sampling;

    O-=O;
    // i grows as sqrt(2*count) , see https://www.shadertoy.com/view/fstyR7
    // average texture along a pseudoGaussian ring of radius |p| ~ projection of direction along view axis 
    for(float i=1.; i<r; i+=1./i)
        p *= rot(GOLDEN_ANGLE), // -mat2(.737, .676, -.676, .737),
        O += T(U+p*i*16./r);

    O = sqrt(O/O.a);  // normalize + to sRGB

    vec2 M = iMouse.xy;
  if (iMouse.z>0.|| length(M)<10. ) {  // --- if mouse click, show texture sampling footprint
    O *= .5;                                     // fade previous image
    if ( length(M)<10. ) M = R/2. + R/3.*cos(iTime+vec2(0,33)); // demo mode
    p = vec2( dot( M+M-R , vec2(1,-5)/5e3 ), 0); // projection of direction along view axis 
    r = sampling;
    for(float i=1.; i<r; i+=1./i)
         p *= rot(GOLDEN_ANGLE), // -mat2(.737, .676, -.676, .737),
        O = max( O, smoothstep(1.5,0.,length( M+p*i*5.*16./r - U )) );
  }
  if (int(U)==int(R)/2) O = vec4(1,0,0,1);       // red separator
}
