// #define GOLD_NOISE // Uncomment this line to replace the classic white noise with the gold noise

#ifdef GOLD_NOISE

// Gold Noise ©2015 dcerisano@standard3d.com
// - based on the Golden Ratio
// - uniform normalized distribution
// - fastest static noise generator function (also runs at low precision)

float PHI = 1.61803398874989484820459;  // Φ = Golden Ratio   
float seed = 1.;
float random(in vec2 xy) {
       return fract(tan(distance(xy*PHI, xy)*seed)*xy.x);
}

#else

float random(vec2 uv){
    return fract(sin(dot(uv, vec2(12.9898,78.233))) * 43758.5453);
    // Or another version of the white noise (doesn't work properly either) :
    // uv = fract(uv * vec2(155.65, 121.56));
    // uv += dot(uv, uv + 32.45);
    // return fract(uv.x * uv.y);
}

#endif

// Perlin noise that uses the white noise
// Allows us to benchmark the quality and reliability of the white noise distribution
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));
    vec2 u = f * f * (3.0 - 2.0 * f);
    return mix(a, b, u.x) + (c - a)* u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    fragColor = vec4(vec3(noise(uv * 10. + 1.)), 1.);
}
