void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-.5;
    float k = iResolution.x/iResolution.y; //monitor ratio (basically 16/9)
    uv.x *= k;

    float a = abs(sin(iTime))*(uv.y/tan(uv.y))*(uv.x/tan(uv.x));
    fragColor = vec4(a,a,a,1.0);
}
