{
	fragColor = texture(iChannel0,  fragCoord.xy / iResolution.xy);
}

// ying yang feedback from Taichi2D

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.0)*1.2 /iResolution.yy;

    float t = clamp(4.*sin(iTime/2.),-3.1416,3.1416) +1.5708; 
    vec2 tpos = .25*vec2(sin(t),cos(t)); 

    float h = 0.;
    float x = distance(uv, tpos), y = distance(uv, -tpos);
    if(y<.25 ) h=-1. +smoothstep(.24,.25,y); // +antialiasing
    if(x<.25 ) h= 1. -smoothstep(.24,.25,x); //    
    if(x<.10 ) h=-1. +smoothstep(.09,.10,x); //
    if(y<.10 ) h= 1. -smoothstep(.09,.10,y); // 

    if(length(uv)<.5) h = clamp(h+ texture(iChannel0,fragCoord/iResolution.xy).x, -1., 1. );
    fragColor = vec4(h,h,h+1.,1.);
}
