#define S(v)   smoothstep( 3./R.y, 0., v )
#define C(x,y) O += S( abs( length(U-vec2(x,y)) - .1 ) )
#define D(x,y) O += S(      length(U-vec2(x,y)) - .08  )

void mainImage( out vec4 O, vec2 u )
{
    O-=O;
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y;

    C(-.5,.5);
    if mouse_down     D(-.5,.5);    // mouse down event 
    C( 0.,.5);
    if mouse_up       D( 0.,.5);    // mouse up event 
    C( .5,.5);
    if mouse_biclick  D( .5,.5);    // bi-click event 
    C(-.25,.8);
    if (iMouse.z>.0)  D(-.25,.8);   // mouse pressed

    O += T(u);
}

void mainImage( out vec4 O, vec2 U )
{
    // --- mouse states management 

    if ( U==vec2(.5) ) 
        O = iMouse;                                                 // previous mouse state

    if ( U==vec2(1.5) ) 
        O = vec4( iMouse.w > 0.,                                    // down event
                  iMouse.z < 0. && T(0).z > 0.,                     // up event
                  iMouse.w > 0. && iTime -T(1).w < BICLICK_DELAY,   // bi-click-event
                  T(1).y > 0. ? iTime : T(1).w );                   // time of last up event


    // --- time line visualisation

    if ( U.x > 2. ) {
        vec2 R = iResolution.xy;
        O = T( U + vec2(0,1) );
        if ( int(U.y) == int(R.y*.6) && abs(U.x/R.x-.5) < .3 ) 
              O = vec4( T(1)[int(3.*(U.x/R.x-.2)/.6)]  );
    }
}
