// Fork of https://shadertoy.com/view/fdsGz8

void mainImage( out vec4 O, vec2 U )
{
    float L = iResolution.x/2.,
          z = exp2(1.5*(cos(iTime)-1.));                        // stretching
    U = mod(U,L) * z;                                           // stretched z
    O-=O;
    if ( max(fract(U.x),fract(U.y)) < z )                       // first pixel in a stretched texel
        O =  texelFetch(iChannel0, ivec2(U) % textureSize(iChannel0,1).xy, 1 ); 

}
