float shine_f(){
    float x = iTime;
    return pow(sin(x/5.)*sin(x/2.)*sin(2.*x), 2.) + .2;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy;
    vec2 q = p - vec2(.5, .5);
    q.x *= iResolution.x / iResolution.y;

    vec3 sky = vec3(7./255., 18./255., 27./255.);    
    vec3 shine = vec3(119./255., 199./255., 242./255.);

    float curve = 1.;
    curve = exp(length(q) * 5.) - 1.3;
    curve *= shine_f();
    curve *= 6.;
    curve += cos((atan(q.x, q.y) + iTime) * 6.) * .3; 
    shine = mix(vec3(1.), shine, curve);

    fragColor = vec4(shine, 1.0);
}
