const vec3 CAMERA_POS = vec3(0, 1.5, -6);
const float INF = 1e10;
const int
    EMISSION = 0,
    DIFFUSE = 1, 
    REFLECTION = 2,
    REFRACTION = 3,
    FOG = 4;

const vec3 LIGHT2_POS = vec3(-3, 1.5, 5);
const float LIGHT2_R = 0.4;
const vec3 LIGHT2_C = vec3(1, 1, 0);

const vec3 LIGHT1_POS = vec3(2, 1, -1);
const float LIGHT1_R = 0.2;
const vec3 LIGHT1_C = vec3(0.1, 0.6, 0.6);

const float AIR_N = 1.0;
const float GLASS_N = 1.5;

const float MARCH_SIZE = 0.1;

float tracePlane(vec3 pos, vec3 dir, out vec3 normal) {
    float t = (-1.15 - pos.y) / dir.y;
    if (t <= 0.0) {
        return INF;
    }
    vec3 worldPos = t * dir + pos;

    if (dot(worldPos.xz, worldPos.xz) >= 100.0) {
        return INF;
    }
    normal = vec3(0, 1, 0);
    return t;

}

float traceSphere(vec3 pos, vec3 dir, float r, out vec3 normal) {
    float a = dot(dir, dir);
    float b = dot(pos, dir);
    float c = dot(pos, pos) - r * r;
    float D = b * b - a * c;
    if (D < 0.0) {
        return INF;
    }
    float t = -b - sqrt(D);
    if (t > 0.0) {
        normal = normalize(pos + t * dir);
        return t;
    }
    t = -b + sqrt(D);
    if (t < 0.0) {
        return INF;
    }
    normal = normalize(pos + t * dir);
    return t;
}

float traceCylinder(vec3 pos, vec3 dir, out vec3 normal) {
    float t = (-1.0 - pos.y) / dir.y;
    if (t <= 0.0) {
        return INF;
    }
    vec3 worldPos = t * dir + pos;
    if (dot(worldPos.xz, worldPos.xz) < 0.5) {
        normal = vec3(0, 1, 0);
        return t;
    }
    float a = dot(dir.xz, dir.xz);
    float b = dot(pos.xz, dir.xz);
    float c = dot(pos.xz, pos.xz) - 0.5;
    float D = b * b - a * c;
    if (D < 0.0) {
        return INF;
    }
    t = (-b - sqrt(D)) / a;
    if (t > 0.0) {
        worldPos = t * dir + pos;
        if (worldPos.y <= -1.0) {
            normal = normalize(vec3(worldPos.x, 0, worldPos.z));
            return t;
        }
    }
    t = (-b + sqrt(D)) / a;
    if (t < 0.0) {
        return INF;
    }
    worldPos = t * dir + pos;
    if (worldPos.y <= -1.0) {
        normal = normalize(vec3(worldPos.x, 0, worldPos.z));
        return t;
    }
    return INF;
}

vec3 computeLight(vec3 pos, vec3 color, vec3 normal) {
    vec3 toLight1 = LIGHT1_POS - pos;
    float distSq1 = dot(toLight1, toLight1);
    float att1 = 20.0f / distSq1;
    vec3 toLight2 = LIGHT2_POS - pos;
    float distSq2 = dot(toLight2, toLight2);
    float att2 = 10.0f / distSq2;
    return color * (
        max(0.0, dot(normal, normalize(toLight1))) * att1 * LIGHT1_C
        + max(0.0, dot(normal, normalize(toLight2))) * att2 * LIGHT2_C
        + texture(iChannel1, normal).rgb * 0.1
    );
}

vec3 refraction(vec3 v, vec3 normal, float n1, float n2) {
    if (dot(v, normal) < 0.0) {
        normal = -normal;
    }
    float cosA = dot(v, normal);
    float sinA = sqrt(1.0 - cosA * cosA);
    vec3 tang = normalize(v - cosA * normal);
    float sinB = sinA * n1 / n2;
    float cosB = sqrt(1.0 - sinB * sinB);
    return sinB * tang + cosB * normal;
}




float pow2(float x) {
    return x * x;
}

float rand(float frame)
{
    return fract(sin( dot(vec3(frame), vec3(12.9898,78.233,45.5432) )) * 43758.5453);
}

vec3 random3(vec3 c) {
	float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
	vec3 r;
	r.z = fract(512.0*j);
	j *= .125;
	r.x = fract(512.0*j);
	j *= .125;
	r.y = fract(512.0*j);
	return r-0.5;
}

const float F3 =  0.3333333;
const float G3 =  0.1666667;


float simplex3d(vec3 p) {
	 vec3 s = floor(p + dot(p, vec3(F3)));
	 vec3 x = p - s + dot(s, vec3(G3));

	 vec3 e = step(vec3(0.0), x - x.yzx);
	 vec3 i1 = e*(1.0 - e.zxy);
	 vec3 i2 = 1.0 - e.zxy*(1.0 - e);

	 vec3 x1 = x - i1 + G3;
	 vec3 x2 = x - i2 + 2.0*G3;
	 vec3 x3 = x - 1.0 + 3.0*G3;

	 vec4 w, d;

	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);

	 w = max(0.6 - w, 0.0);

	 d.x = dot(random3(s), x);
	 d.y = dot(random3(s + i1), x1);
	 d.z = dot(random3(s + i2), x2);
	 d.w = dot(random3(s + 1.0), x3);

	 w *= w;
	 w *= w;
	 d *= w;

	 return dot(d, vec4(52.0));
}

const mat3 rot1 = mat3(-0.37, 0.36, 0.85,-0.14,-0.93, 0.34,0.92, 0.01,0.4);
const mat3 rot2 = mat3(-0.55,-0.39, 0.74, 0.33,-0.91,-0.24,0.77, 0.12,0.63);
const mat3 rot3 = mat3(-0.71, 0.52,-0.47,-0.08,-0.72,-0.68,-0.7,-0.45,0.56);

float simplex3d_fractal(vec3 m) {
    return   0.5333333*simplex3d(m*rot1)
			+0.2666667*simplex3d(2.0*m*rot2)
			+0.1333333*simplex3d(4.0*m*rot3)
			+0.0666667*simplex3d(8.0*m);
}



void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 randVals = vec3(rand(float(iFrame)), rand(float(iFrame + 5)), rand(float(iFrame + 15)));
    vec2 uv = (fragCoord - iResolution.xy * 0.5 + (randVals.xy - 0.5)) / iResolution.x;
    vec3 front = normalize(-CAMERA_POS);
    vec3 up = vec3(0, 1, 0);
    vec3 right = normalize(cross(front, up));
    up = normalize(cross(right, front));
    vec3 viewVec = normalize(front + right * uv.x + up * uv.y);
    vec3 curPos = CAMERA_POS;
    vec3 curDir = viewVec;
    float n1 = AIR_N;
    float n2 = GLASS_N;
    float GLASS_R = pow2(AIR_N - GLASS_N) / pow2(AIR_N + GLASS_N);

    float mult = 1.0;
    vec3 p3 = vec3(uv, 0.025);

	float value;


	value = simplex3d_fractal(p3*8.0+8.0);
	value = 0.5 + 0.5 * value;

	fragColor = vec4(vec3(value), 1.0);
    for (int i = 0; i < 15; ++i) {
        vec3 normal;
        vec3 planeNorm;
        int materialType;
        vec3 color;
        float t = INF;
        float planeT = tracePlane(curPos, curDir, planeNorm);
        if (planeT < t) {
            t = planeT;
            materialType = DIFFUSE;
            vec3 worldPos = t * curDir + curPos;
            color = texture(iChannel0, worldPos.xz * 0.2).rgb;
            normal = planeNorm;
            if (randVals.y < 0.15) {
                materialType = REFLECTION;
            }
        }
        vec3 l1Norm;
        float light1T = traceSphere(curPos - LIGHT1_POS, curDir, LIGHT1_R, l1Norm);
        if (light1T < t) {
            t = light1T;
            materialType = EMISSION;
            color = LIGHT1_C;
            normal = l1Norm;
        }
        vec3 l2Norm;
        float light2T = traceSphere(curPos - LIGHT2_POS, curDir, LIGHT2_R, l2Norm);
        if (light2T < t) {
            t = light2T;
            materialType = EMISSION;
            color = LIGHT2_C;
            normal = l2Norm;
        }
        vec3 sphNorm;

        float sphT = traceSphere(curPos, curDir, 1.0, sphNorm);
        if (sphT < t ) {
            t = sphT;
            normal = sphNorm;
            if (randVals.x < GLASS_R) {
                materialType = REFLECTION;
            } else if (randVals.x >= GLASS_R && randVals.x < 0.25){
                materialType = REFRACTION;
            } else {
                materialType = FOG;
            }

        }
        vec3 cylNorm;
        float cylT = traceCylinder(curPos, curDir, cylNorm);
        if (cylT < t) {
            t = cylT;
            materialType = DIFFUSE;
            vec3 worldPos = t * curDir + curPos;
            color = texture(iChannel2, worldPos.xz * worldPos.y).rgb;
            normal = cylNorm;

        }
        if (t != INF) {
            vec3 worldPos = t * curDir + curPos;
            fragColor = texture(iChannel0, worldPos.xz);
            if (materialType == EMISSION) {
                fragColor.rgb = color;
                break;
            } else if(materialType == FOG) {
                fragColor.rgb = vec3(1, 1, 0) * value;
            } else if (materialType == DIFFUSE) {
                fragColor.rgb = computeLight(worldPos, color, normal);
                break;
            } else if (materialType == REFLECTION) {
                curDir = reflect(curDir, normal);
                curPos = worldPos + 1e-3 * curDir; 


            } else if (materialType == REFRACTION) {
                curDir = refraction(curDir, normal, n1, n2);
                curPos = worldPos + 1e-3 * curDir;

                float tmp = n1;
                n1 = n2;
                n2 = tmp;

            }
        } else {
            fragColor = texture(iChannel1, curDir);

        }
    }

}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = texture(iChannel0, uv) + texture(iChannel1, uv);
}
