// A/B monitor viewer Wipe UI demo
// Demonstrates viewing 2 input colors with mouse interaction
//
// Just click into the screen to pick the center and move the mouse along the line
// You can also rotate the mouse without releasing the left button to change the angle
//
// MonA and MonB simulate 2 texture inputs

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 MonA = texture(iChannel0, fragCoord.xy / iResolution.xy).rgb;
    vec3 MonB = texture(iChannel1, fragCoord.xy / iResolution.xy).rgb;

    vec2 a; // first clicked point
    vec2 b; // mouse move without releasing the button
    if (iMouse == vec4(0.0)) {
        a = 0.1 * iResolution.xy;
        b = iResolution.xy;
    } else {
        a = abs(iMouse.zw);
        b = iMouse.xy;
    }

    // Calculate mouse move vector.
    vec2 ba = b - a;

    // Rotate the vector by 90 degrees trick :)
    ba = vec2(ba.y, -ba.x);

    float t = dot(fragCoord - a, ba);
    t = clamp(t, 0., 1.);
    vec3 color = mix(MonA, MonB, t);

    fragColor = vec4(color, 1.0);
}
