#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

void mainImage(out vec4 color,in vec2 coord){float R=12.2;
  vec2 xy = coord-iResolution.xy/2.0;
  vec2 oxy=xy;xy=vec2(xy.y,xy.x);
  float r=sqrt(xy.x*xy.x+xy.y*xy.y);
  float spiral=xy.x*cos(r/R)+xy.y*sin(r/R);
  color=vec4(0.0,0.75,0.5,0);
  if(xy.x<-80.0)color=vec4(1,1.0,0.0,0);
  if(r<120.0&&xy.x>-50.0)color=vec4(0.5)*vec4(1,0.5,0,1);
  if(xy.x+100.0-cos(xy.y/120.0)*60.0<20.0&&xy.x-cos(xy.y/40.0)*10.0>-120.0&&abs(xy.y)<320.0&&xy.y<220.0+sin((xy.x+17.0)/5.0)*10.0)color=vec4(0.75);
  if(r<80.0&&xy.x>-60.0)color=vec4(0.5)*vec4(1,0.5,0,1);
  if(spiral<0.0&&r<120.0)color=vec4(0.25)*vec4(1,0.5,0,1);
  xy.y-=250.0;xy.y-=50.0;xy*=rot(-0.5);xy+=80.0;
  if(abs(xy.y)<abs(xy.x-20.0)/10.0&&xy.x<0.0&&xy.x>-100.0||length(xy)<2.0)color=vec4(0.75);
  xy-=80.0;xy*=rot(0.5);xy.t+=80.0;
  xy.y-=20.0;xy.x+=50.0;xy*=rot(-1.0);
  if(abs(xy.y)<abs(xy.x-20.0)/12.0&&xy.x<0.0&&xy.x>-100.0||length(xy)<2.0)color=vec4(0.75);

  if(abs(oxy.y)>180.||abs(oxy.x)>320.0)color=vec4(0);
}
