#define hardness 1
//anything outside of -1 to 1 is unusable
//negative values = softer
//positive values = harder
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float m = iMouse.x/iResolution.y;
    vec2 uv = fragCoord/iResolution.yy;
    vec4 col = vec4(0.0);
    vec4 temp = vec4(0.0);
    for(float i = 1.0;i<=512.0;i*=1.1) {
        temp = texture(iChannel0,uv/i+i);
        col = col+( temp* pow(i,float(-hardness)) );
    }
    col = vec4(
    (normalize(col).r-0.4)*10.0
    );
    fragColor = vec4(
    col
    );
}
