#define R iResolution.xy
#define M iMouse
#define circle(cl,cn,r)  col=mix(col,cl, smoothstep(.001,0.00,length((uv-cn))-r));
#define cc(cl,FUNC)  col=mix(col,cl, smoothstep(.001,0.00,FUNC));

float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float shape(vec2 uv, float k, vec2 c1,float r1, vec2 c2, float r2,vec2 c3, float r3){
    return smin(length(uv-c1)-r1, smin(length(uv-c2)-r2,length(uv-c3)-r3,k),k);    
}

void mainImage( out vec4 O, in vec2 U )
{

    vec2 uv = (U-R*.5)/R.y, m = (M.xy-R*.5)/R.y,m2 = (M.xy-R*.5)/R.y;    
    float a =  iTime*12.,  k=cos(a-.3)*.9+1.7,  k2=cos(a-1.3)*1.+2.4,  z=1.8;
    vec2 v=  iMouse.z>0.? -m.xy*vec2(.15,.4) : vec2(cos(a)*.6,-sin(a)*.7)*.1,
         w = -v,   r=vec2(cos(k),sin(k)),  ra=vec2(cos(k2),sin(k2));

    uv*=z;
    //background
    vec3 col =uv.y <-.3 ? vec3(.3,.6,0):vec3(0,.5,.8);
    //movement
    uv+=z*vec2(1.2,.3) + z*vec2(-2.4,0)*fract(a*.053)-vec2(0,abs(k)*.1);

    //tail
    circle(vec3(1),vec2(-.15,-.02)+w*.8-v*.1,.05);

    //body
    cc( vec3(1), shape(uv,.06,  vec2(.0)-w*.8,.16,  vec2(.12,.05)-w*.5-v*.5,.12,  vec2(.22,.05)-w*.1-v*.8,.11));

    //face
    cc(vec3(1),shape(uv,.02,   vec2(.4,.11)-v,.05,  vec2(.33,.12)-v,.1,  vec2(.43,.11)-v,.03));
    circle(vec3(.0),vec2(.35,.16)+v,.01);   

    //ears
    cc(vec3(0.8,0.7,0.7),shape(uv,.05,  vec2(.27,.35)-v,.04,   vec2(.28,.28)-v,.03,  vec2(.29,.23)-v,.02));    
    cc(vec3(0.9,0.8,0.9),shape(uv,.05,  vec2(.23,.33)-v,.04,   vec2(.25,.26)-v,.03,   vec2(.27,.21)-v,.02));  

    //legs
    cc(vec3(1,.9,.9),shape(uv,.05,   vec2(-.02,-.07)-w,.12,   vec2(-.02,-.07)-w-r*.14,.05,  vec2(-.02,-.07)-w-r*.19,.03));

    //arm
    cc(vec3(1,.9,.9),shape(uv,.04,   vec2(.22,-.01)-v,.07,   vec2(.22,-.01)-v-ra*.07,.04,   vec2(.22,-.01)-v-ra*.12,.03));    

    O = vec4(col,1.0); 
    //vignette
    O *=  pow((1. - U.y / R.y) *U.x / R.x*(1. - U.x / R.x) *U.y / R.y * 10.0, 0.15)* vec4(1.,.7,.5,0);   
}
