float glfact = 0.5;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 duv = 0.5*uv+0.5;

    float fact = 0.5*(cos(1.0-(0.05*sqrt(pow(iMouse.x-fragCoord.x,2.0)+pow(iMouse.y-fragCoord.y,2.0)))) + 1.0);

    vec2 newuv = (fact*glfact)*duv + (1.0-fact*glfact)*uv;

    vec3 col = texture(iChannel0,newuv).rgb;

    fragColor = vec4(col,1.0);
    //fragColor = vec4(newuv,1.0,1.0);
    //fragColor = vec4(fact,fact,fact,1.0);
}
