#define iR (iResolution.xy)
#define PIXW (1./iR.y)
#define UV (I/iR)
#define LEVEL 8

vec4 rowSamp(in vec2 uv,float pixw,sampler2D ch)
{
    float[] off=
    float[](-4.,-3.,-2.,-1.,0.,1.,2.,3.,4.);
    float[] w= //weight
    float[](
0.01621622, 0.05405405, 0.12162162, 0.19459459, 0.22702703,
0.19459459, 0.12162162, 0.05405405, 0.01621622);
    vec4 o;o-=o;
    for(int i=0;i<9;i++)
        o+=texture(ch,
        uv+vec2(off[i]*pixw,0.))*w[i];
    return o;
}

vec4 colSamp(in vec2 uv,float pixw,sampler2D ch)
{
    float[] off=
    float[](-4.,-3.,-2.,-1.,0.,1.,2.,3.,4.);
    float[] w=
    float[](
0.01621622, 0.05405405, 0.12162162, 0.19459459, 0.22702703,
0.19459459, 0.12162162, 0.05405405, 0.01621622);
    vec4 o;o-=o;
    for(int i=0;i<9;i++)
        o+=texture(ch,
        uv+vec2(0.,off[i]*pixw))*w[i];
    return o;
}

void mainImage( out vec4 O, in vec2 I )
{
    O=rowSamp(UV,PIXW,iChannel0);
}

void mainImage( out vec4 O, in vec2 I )
{
    O=colSamp(UV,PIXW,iChannel0);
}

void mainImage( out vec4 O, in vec2 I )
{
    O=texture(iChannel0,UV);

    if(iFrame==0)
        O=texture(iChannel3,UV);

    if(iFrame>LEVEL)
        O=texture(iChannel1,UV);
}
