void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord - (iResolution.xy / vec2(2.0));
    float time = mod(iTime, 12.5);
   	float t = pow(1.25, time);
    float x = float(coord.x)+64.0*sin((coord.x+coord.y)/60.0);
    float y = float(coord.y)+64.0*cos((coord.x-coord.y)/60.0);
    float x2 = float(coord.x)+32.0*sin((coord.x+coord.y)/60.0);
    float y2 = float(coord.y)+32.0*cos((coord.x-coord.y)/60.0);
    float r = float(x*x*t + y*y*t);
    float r2 = float(x2*x2*t + y2*y2*t);
    float f = abs(r);
    float f2 = abs(r2);
    int a = int(floor(f*pow(16.0, 6.0-ceil(log2(r)/4.0))));
    int a2 = int(floor(f2*pow(16.0, 6.0-ceil(log2(r2)/4.0))));
    int A = a&a2;
    fragColor = vec4(
        float((A >> 16) & 255) / 255.0,
        float((0 >> 8) & 255) / 255.0,
        float((A >> 0) & 255) / 255.0,
        1.0
    );
}
