#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
void mainImage(out vec4 c,in vec2 o){
    o-=iResolution.xy/2.0;
    o*=rot(iTime/4.0);
    float x=0.5*o.x+sqrt(3.0)/2.0*o.y;
    float y=0.5*o.x-sqrt(3.0)/2.0*o.y;
    float z=x+y;
    float l1=length(vec2(x,y));
    float l2=length(vec2(x,z));
    float l3=length(vec2(z,y));
    float t=4.0/(sin(iTime/8.0));
    float sum=sin(l1/t)+sin(l2/t)+sin(l3/t);
    float mult=sin(l1/t)*sin(l2/t)*sin(l3/t);
    c=vec4(sum,abs(sum*mult),mult,1);
}
