float H(float v, float n, float x) {
    return (sin(v) * 0.5 + 0.5) * (x - n) + n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pi = acos(-1.0);

    float m = min(iResolution.x, iResolution.y);
    vec2 res = iResolution.xy / m;
    float s = 500.0;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/m;

    uv = (uv - res * 0.5) * s;
    float q = atan(uv.y, uv.x) * 0.5 / pi;
    float l = pow(length(uv), 0.35);
    float r = mod(l + iTime * 0.271 + q, 1.0);
    float g = mod(l + iTime * 0.523 + q, 1.0);
    float b = mod(l + iTime * 0.741 + q, 1.0);

    float d = sin(length(uv) * 0.01 + iTime * 4.0) * 0.25 + 0.75; //length(uv) * 0.85 / (res.y * s) + 0.15;

    // Time varying pixel color
    vec3 col = vec3(r, g, b) * d;

    // Output to screen
    fragColor = vec4(col,1.0);
}
