#define TAU 6.2831853071

vec2 fromAngle( in float n ){
    return vec2(cos(n), sin(n));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float px = 1./iResolution.y;

    float thiccness = px*1.;
    // Time varying pixel color

    float amt = 12.;
    float pie = TAU/(amt*2.);

    float sum = 0.;
    for(float i=0.; i<amt; i++){
        sum+=1.0-smoothstep(thiccness, thiccness+px, abs(dot(uv+vec2(-0.5), fromAngle(iTime*TAU/8.+i*pie))));
    }
    vec3 col = vec3(sum);
    // Output to screen
    fragColor = vec4(col,1.0);
}
