float radius = 16.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 squarePos = fract(fragCoord / radius / 2.0);
    vec2 dist = squarePos - 0.5;
    float distanceFactor = clamp(length(dist) / sqrt(0.5), 0.0, 1.0);

    distanceFactor = 1.0 - distanceFactor; //comment out to invert

    fragColor = vec4(distanceFactor, distanceFactor, distanceFactor, 1.0);
}
