#define res iResolution.xy
const float tau = 6.283185307179586;

vec2 cossin(float t) {
    return vec2(cos(t), sin(t));
}

mat2 rot(float t) {
    vec2 cs = cossin(t);
    return mat2(cs.x, -cs.y, cs.y, cs.x);
}

void mainImage(out vec4 col, vec2 coord) {
    vec2 pos = (coord * 2. - res) / res.y;
    int i;
    for(i = 0; i < 64; i++) {
        float depth = float(i)+1.-fract(iTime*3.);
        float z = float(i)+floor(iTime*3.);
        vec2 op = rot(z*0.2) * (pos * depth * .5 + cossin((iTime*3.-1.)*.2)*2.) + .25;
        if (fract(op.x) > .5 ^^ fract(op.y) > .5) break;
    }
    col = vec4(sqrt( (cos((vec3(0,1,2)/3. + (float(i)+floor(iTime*3.)) * .03) * tau) * .5 + .5) * exp(-(float(i)+1.-fract(iTime*3.))*.2) ),1);
}
