#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

float GetDist(vec3 p)
{
    vec4 s = vec4(0., 1., 6., 1.);

    float sphere_dist = length(p - s.xyz) - s.w;
    float plane_dist = p.y;

    float d = min(sphere_dist, plane_dist);
    return d;
}

float RayMarch(vec3 ro, vec3 rd)
{
    float dO = 0.;

    for(int i = 0; i <= MAX_STEPS; i++)
    {
        vec3 p = ro + dO * rd;
        float ds = GetDist(p);
        dO += ds;
        if(dO>MAX_DIST || ds < SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p)
{
    float d = GetDist(p);
    vec2 e = vec2(.01, 0.);

    vec3 n = d - vec3(
             GetDist(p - e.xyy),
             GetDist(p - e.yxy),
             GetDist(p - e.yyx));
    return normalize(n);
}

float GetLight(vec3 p)
{
    vec3 lightPos =  vec3(0, 4, 6);
    lightPos.xz += vec2(sin(iTime), cos(iTime) * 3.);
    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(l, n), 0., 1.);

    float d = RayMarch(p+n*SURF_DIST*2.,l);
    if(d < length(lightPos - p))
        dif *= .1;

    return dif;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2. *fragCoord.xy - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.);

    vec3 ro = vec3(.0, 1., .0);
    vec3 rd = vec3(uv.x, uv.y, 1.);

    float d = RayMarch(ro, rd);

    vec3 p /*Surface Position*/ = ro + rd * d;

    float dif = GetLight(p);

    col = vec3(dif);

    fragColor = vec4(col,1.0);
}
